(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var rightNow = new GlideDateTime();
	var whenNeeded = new GlideDateTime(current.u_when_needed);

	if(whenNeeded.before(rightNow))
		{
			gs.addErrorMessage('The entered date is in the past,Your request has not saved to the Database. Kindly enter a future date');
			current.setAbortAction(true);
		}

	var today = rightNow.getLocalDate();
	var isToday = whenNeeded.getLocalDate();
if (today==isToday)
{
gs.addErrorMessage('The entered date needs to be greater than today');
current.setAbortAction(true);
}

gs.info('this is a info log');
gs.warn('this is a warn log');

var a=10;
var b=15;

sumAandB(a,b);

var isEmail = validateEmail(current.u_requested_for_email);
if (isEmail == false)
	{
		gs.addErrorMessage('Invalid Email Address');
		current.setAbortAction(true);
	}

})(current, previous);
